import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import reportWebVitals from './reportWebVitals';
import "bulma/css/bulma.css"
import { initializeApp } from "firebase/app";
import { getFirestore, 
  collection, getDoc, 
  addDoc, deleteDoc, doc,
  onSnapshot
} from "firebase/firestore";


const firebaseConfig = {
  apiKey: "AIzaSyD-93dTvcqceIgA7eiJ-ZTGGuP0pgSfR8I",
  authDomain: "ecommerce-f2425.firebaseapp.com",
  projectId: "ecommerce-f2425",
  storageBucket: "ecommerce-f2425.appspot.com",
  messagingSenderId: "342964477004",
  appId: "1:342964477004:web:0c45cbd1a73981775b68e9"
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

const colRef = collection(db, 'paintings')

const docSnap = await getDoc(colRef);

if (docSnap.exists()) {
  console.log("Document data:", docSnap.data());
} else {
  // doc.data() will be undefined in this case
  console.log("No such document!");
}



// real time collection data
onSnapshot(colRef, (snapshot) => {
  let paintings = []
  snapshot.docs.forEach((doc) => {
    paintings.push({ ...doc.data(), id: doc.id })
  })
  console.log(paintings)
})

// adding documents
const addToCart = document.querySelector('.add')
addToCart.addEventListener('submit', (e) => {
  e.preventDefault()

  addDoc(colRef, {
    title: addToCart.title.value,
    author: addToCart.author.value
  })
  .then(() => {

  })
})

// deleting documents
const deleteFromCart = document.querySelector('.delete')
deleteFromCart.addEventListener('submit', (e) => {
  e.preventDefault()

  const docRef = doc(db, 'paintings', deleteFromCart.id.value)
  deleteDoc(docRef)
    .then(() => {
      deleteFromCart.reset()
  })
})



const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
