import React from "react";
import { useParams } from "react-router-dom";
import withContext from "../withContext";

const ProductDetails = props => {
    const { products } = props.context
    const { title } = useParams()
    return(
        <div>
            {
                products.filter((product) => product.title === title)
                .map((product, index) => (
                            <div className="painting-container">
            <div className="painting-image">
                <figure className="image">
                    <img 
                        src={product.img}
                        alt={product.shortDesc}
                    />
                </figure>
            </div>
            <div className="painting E-content">
                <div className="painting-text">
                    <h1 className="painting-title">
                        {product.title}
                    </h1>
                    <h2 className="painting-artist">{product.artist}</h2>
                    <span className="painting-price">{product.price}</span>
                    {product.stock > 0 ? (
                    <small className="painting-stock">{product.stock + " Available"}</small>
                    ) : (
                    <small className="out-of-stock">Out Of Stock</small>
                    )}
                </div>
                <div className="painting-buttons">
                    <button
                        className="add-to-cart"
                        onClick={() => 
                            props.addToCart({
                                id: product.id,
                                title: product.title,
                                img: product.img,
                                link: product.link,
                                description: product.description,
                                medium: product.medium,
                                artist: product.artist,
                                quantity: 1,
                                stock: product.stock,
                                price: product.price
                            })
                        }
                    >
                        Add to Cart
                    </button>
                </div>
            </div>
        </div>
                ))
            }
        </div>
    )
}

export default withContext(ProductDetails)
