import React from "react";
import withContext from "../withContext";
import CartItem from "./CartItem"

const Cart = props => {
   const { cart } = props.context
   const checkbtn = document.querySelector("input[name=checkbox]");
   const totalAmount = document.getElementById("total-amount")
   const freeText = document.getElementById("free")


   function numberWithCommas(x) {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
    }

    function multipleItemCheck() {
        if (props.context.numberOfItems) {
            if (props.context.numberOfItems === 1) {
                return " item"
            } else {
                return " items"
            }
        } else {
            return 0
        }
    }

    checkbtn.addEventListener('change', e => {
        if (e.target.checked) {
            totalAmount.classList.add("crossout")
            freeText.style.display = "inherit"
        } else {
            totalAmount.classList.remove("crossout")
            freeText.style.display = "none"
        }
    } )



   console.log(cart)
   return(
    <>
    <div className="title-container">
        <h4 className="page-title">Cart</h4>
    </div>
    <div className="cart-container">
        <div className="cartitems">
            <div className="cartitem-list">
                <div className="cart-categories">
                    <p>Product</p>
                    <p className="category-price">Price</p>
                </div>
            {cart && cart.length ? (
                cart.map((product, index) => (
                        <CartItem
                            product={product}
                            key={index}
                            removeFromCart={props.context.removeFromCart}
                        />
                    ))
            ) : (
                <div className="column">
                    <div className="title has-text-grey-light">No item in cart!</div>
                </div>
            )}
            </div>
        </div>
        <div className="total-container">
            <h3 className="total-text">Total ({props.context.numberOfItems + multipleItemCheck()}):</h3>
            <h3 className="total-amount" id="total-amount">${numberWithCommas(props.context.total)}</h3>
            <h4 className="free" id="free">FREE</h4>
            <div className="checkbox-container">
                <input type="checkbox" id="free-total" name="free-total" value="false" />
                <label for="free-total">Bridger is a great programmer</label>
            </div>
            <button
                className="checkout-button"
                onClick={props.context.checkout}
            >
                Checkout
            </button>
        </div>
    </div>
    </>
   ) 
}

export default withContext(Cart)
