import React from "react";
import { useParams } from "react-router-dom";

const ProductDetails = props => {
    const { product } = props.context.products
    const { id } = useParams()
    return(
        <div>
            {
                product.filter((products) => products.id === id)
                .map((products) => (
                    <div key={products.id}>
                        <h2>{products.title}</h2>
                    </div>
                ))
            }
        </div>
    )
}

export default ProductDetails
